import React, { useState } from "react";
// import ListData from "./ListData";
import "./style.css";
const Todoapp = () => {
  const [search, setSearch] = useState("");
  const [leftData, setLeftData] = useState([]);
  const [rightData, setRightData] = useState([]);
  const Handler = (event) => {
    setSearch(event.target.value);
  };
  const Add = () => {
    var obj = { id: Math.random(), value: search, isSelected: false };
    setLeftData((prevData) => {
      return [...prevData, obj];
    });
    setSearch("");
  };
  const onSelect = (collection , id, side) => {
    collection = collection.map((item)=>{
      if(item.id === id)item.isSelected = !item.isSelected;
      return item;
    })
    side === "left" ? setLeftData(collection) : setRightData(collection);
  };
  const LeftToRight = () => {
    var leftCollection = leftData;
    var moveObjCollection = [];
    leftCollection = leftCollection.filter((item) => {
      if (item.isSelected) {
        item.isSelected = false;
        moveObjCollection.push(item);
        return false;
      }
      return true;
    });
    setLeftData(leftCollection);
    setRightData([...rightData, ...moveObjCollection]);
  };
  const RightToLeft = () => {
    var rightCollection = rightData;
    var moveObjCollection = [];
    rightCollection = rightCollection.filter((item) => {
      if (item.isSelected) {
        item.isSelected = false;
        moveObjCollection.push(item);
        return false;
      }
      return true;
    });
    setRightData(rightCollection);
    setLeftData([...leftData, ...moveObjCollection]);
  };
  const AllDataMoveLeftToRight = () => {
    setRightData([...rightData, ...leftData]);
    setLeftData([]);
  };
  const AllDataMoveRightToLeft = () => {
    setLeftData([...leftData, ...rightData]);
    setRightData([]);
  };
  return (
    <div>
      <h2>Notes Anything here.</h2>
      <input
        type="text"
        placeholder="search"
        value={search}
        onChange={Handler}
      />
      <button onClick={Add}> Submit</button>
      <div className="list">
        <div className="listbox">
          <ul>
            {leftData.map((e) => {
              return (
                <li onClick={() => onSelect(leftData, e.id, "left")}>
                  <span className={e.isSelected ? "highlight" : ""}>
                    {e.value}
                  </span>
                </li>
              );
            })}
          </ul>
        </div>
        <div className="btn">
          <button className="btn-data" onClick={() => LeftToRight()}>
            {"->"}
          </button>
          <button className="btn-data" onClick={() => RightToLeft()}>
            {"<-"}
          </button>
          <button
            className="btn-data"
            onClick={() => AllDataMoveLeftToRight(leftData, rightData)}
          >
            {"-->"}
          </button>
          <button
            className="btn-data"
            onClick={() => AllDataMoveRightToLeft(rightData, leftData)}
          >
            {"<--"}
          </button>
        </div>
        <ListData ListData= {ListData} />
        {/* <div className="listbox">
          <ul>
            {rightData.map((e) => {
              return (
                <li onClick={() => onSelect(rightData, e.id, "right")}>
                  <span className={e.isSelected ? "highlight" : ""}>
                    {e.value}
                  </span>
                </li>
              );
            })}
          </ul>
        </div> */}
      </div>
    </div>
  );
};
export default Todoapp;
