import React, { useState } from "react";

const Names = () => {
  const [name, setName] = useState(["  ", "  "]);
  const [value, setValue] = useState("");

  let onSubmit = (event) => {
    event.preventDefault();
    setName([...name, value]);
    setValue("");
  };

  let displayNames = name.map((x, key) => {
    return <div key={key + 1}>{x}</div>;
  });
  console.log(name);
  return (
    <div>
      <form>
          <input className="mt-5 ms-4" name="abcd" value={value} onChange={(e) => setValue(e.target.value)} /><br />
          <button className="mt-5 ms-4" onClick={(event) => onSubmit(event)}>Add</button>
      </form>
      <div className='row mt-5 d-flex justify-content-center'>
          <div className='border border-dark col-3 ms-5 box me-5' >
              {displayNames}
          </div>
          <div className='col-1'>
              <button className="mt-3 ms-4" onClick={(event) => onSubmit(event)}>Right</button>
              <button className="mt-3 ms-4" onClick={(event) => onSubmit(event)}>Left</button>
              <button className="mt-3 ms-4" onClick={(event) => onSubmit(event)}>Ra</button>
              <button className="mt-3 ms-4" onClick={(event) => onSubmit(event)}>Left all</button>
          </div>
          <div className='border border-dark col-3 ms-5 box'>
          </div>
      </div>
  </div>
 );
};

export default Names;
