import React, { useState } from "react";

const Names = () => {
  const [name, setName] = useState(["  ", "  "]);
  const [value, setValue] = useState("");

  let onSubmit = (event) => { 
    event.preventDefault();
    setName([...name, value]);
    setValue("");
  };

  let displayNames = name.map((x, key) => {
    return <div key={key + 1}>{x}</div>;
  });
  console.log(name);
  return (
    <div>
      <form>
        <input value={value} onChange={(e) => setValue(e.target.value)} />
        <button onClick={(event) => onSubmit(event)}>Add</button> <br />
        <button> /= </button> <br />
        <button> \= </button> <br />
        <button> /== </button> <br />
        <button> \== </button> <br />
      </form>
      {displayNames}
    </div>
  );
};

